package com.example.demo;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import java.util.Scanner;@RestControllerpublic class HelloAliyunController {    @RequestMapping("/")    public static int main(String[] args){        return HelloAliyunController.helloAliyun();    }    public static int helloAliyun(){        Scanner scanner = new Scanner(System.in);        int a = scanner.nextInt();        int b = scanner.nextInt();        return a+b;    }}